import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faArrowRight} from '@fortawesome/free-solid-svg-icons';
import '../css/Section.css';

const Section = (props) => {
    const isImg=props.flag;
    let imgvid;
    if(isImg)
    {
       imgvid=<img src={props.link} alt="img"/>
    }
    else{
        imgvid=
        <video source src={props.link} type="video/mp4"/>
    }
    return (
        <div className='section'>
            <div className='row'>
                <div className='col-6'>
                 {imgvid}
                </div>
                <div className='col-6'>
                   <p className='sheader'>{props.title}</p>
                   <p className='content'>{props.content}</p>
                   <button type="button" class="button button1">{props.btntitle} {<FontAwesomeIcon icon={faArrowRight}/>}</button>
                </div>
            </div> 
        </div>
    )
}

export default Section;
