import React from 'react';
import '../css/Header.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faA} from '@fortawesome/free-solid-svg-icons';

const Header = () => {
    return (
        <div>
            <div className='row'>
                 <div className='header col-6'>
                   <div className='row'>
                   <div className='col-4 he1'>
                     <img src="http://assets.stickpng.com/images/5a1aca5ff65d84088faf138f.png" alt="img"/>
                   </div>
                   <div className='col-8 he2'>                 
                   <p>Gift Selection, Simplified</p>
                   <p className='content'>Bring joy to deserving recipients of all stripes with a carefully curated selection of formulations for the skin, body and home. </p>
                   <button type="button" class="button button1">Browse all gifts {<i class="fas fa-arrow-right"></i>}</button>
                   </div>
                   </div>
                  </div>
                  <div className='imgbg col-6'>
                  
                  </div>
            </div>
        </div>
    )
}

export default Header;
